var add = function (value1,value2) {
    return value1 + value2;
}

var subtract = function (value1,value2) {
    return value1 - value2;
}

var multiplay = function (value1,value2) {
    return value1 * value2;
}

var divide = function (value1, value2) {
    return value1 / value2;
}

module.exports = {add,subtract,multiplay,divide};

